import{_ as r}from"./plugin-vue_export-helper.21dcd24c.js";import{o as i,c as a,e as n}from"./app.bda3d84e.js";const e={},p=n('<h1 id="\u4E00\u3001spring\u6982\u8FF0" tabindex="-1"><a class="header-anchor" href="#\u4E00\u3001spring\u6982\u8FF0" aria-hidden="true">#</a> \u4E00\u3001Spring\u6982\u8FF0</h1><p>\u200B Spring \u7684\u4E3B\u8981\u4F5C\u7528\u5C31\u662F\u4E3A <strong>\u4EE3\u7801 \u201C \u89E3\u8026 \u201D</strong>\uFF0C\u964D\u4F4E\u4EE3\u7801\u95F4\u7684\u8026\u5408\u5EA6\u3002\u6839\u636E\u529F\u80FD\u7684\u4E0D\u540C\uFF0C\u53EF\u4EE5\u5C06\u4E00\u4E2A\u7CFB\u7EDF\u4E2D\u7684\u4EE3\u7801\u5206\u4E3A <strong>\u4E3B\u4E1A\u52A1\u903B\u8F91</strong> \u4E0E <strong>\u7CFB\u7EDF\u7EA7\u4E1A\u52A1\u903B\u8F91</strong> \u4E24\u7C7B\u3002</p><p>\u5B83\u4EEC\u5404\u81EA\u5177\u6709\u9C9C\u660E\u7684\u7279\u70B9\uFF1A</p><ul><li>\u4E3B\u4E1A\u52A1\u4EE3\u7801\u95F4\u903B\u8F91\u8054\u7CFB\u7D27\u5BC6\uFF0C\u6709\u5177\u4F53\u7684\u4E13\u4E1A\u4E1A\u52A1\u5E94\u7528\u573A\u666F\uFF0C\u590D\u7528\u6027\u76F8\u5BF9\u8F83\u4F4E\uFF1B</li><li>\u7CFB\u7EDF\u7EA7\u4E1A\u52A1\u76F8\u5BF9\u529F\u80FD\u72EC\u7ACB\uFF0C\u6CA1\u6709\u5177\u4F53\u7684\u4E13\u4E1A\u4E1A\u52A1\u5E94\u7528\u573A\u666F\uFF0C\u4E3B\u8981\u662F\u4E3A\u4E3B\u4E1A\u52A1\u63D0\u4F9B\u7CFB\u7EDF\u7EA7\u670D\u52A1\uFF0C\u5982\u65E5\u5FD7\u3001\u5B89\u5168\u3001\u4E8B\u52A1\u7B49\uFF0C\u590D\u7528\u6027\u5F3A\u3002</li></ul><p>\u200B Spring\u6839\u636E\u4EE3\u7801\u7684\u529F\u80FD\u7279\u70B9\uFF0C\u5C06\u964D\u4F4E\u8026\u5408\u5EA6\u7684\u65B9\u5F0F\u5206\u4E3A\u4E86\u4E24\u7C7B\uFF1AIoC \u4E0E AOP\u3002</p><ul><li><p>IoC \u4F7F\u5F97\u4E3B\u4E1A\u52A1\u5728\u76F8\u4E92\u8C03\u7528\u8FC7\u7A0B\u4E2D\uFF0C\u4E0D\u7528\u518D\u81EA\u5DF1\u7EF4\u62A4\u5173\u7CFB\u4E86\uFF0C\u5373 <strong>\u4E0D\u7528\u518D\u81EA\u5DF1\u521B\u5EFA\u8981\u4F7F\u7528\u7684\u5BF9\u8C61</strong> \u4E86\u3002\u800C\u662F\u7531 Spring \u5BB9\u5668\u7EDF\u4E00\u7BA1\u7406\uFF0C\u81EA\u52A8 \u201C \u6CE8\u5165 \u201D\u3002</p></li><li><p>\u800C AOP \u4F7F\u5F97\u7CFB\u7EDF\u7EA7\u670D\u52A1\u5F97\u5230\u4E86\u6700\u5927\u590D\u7528\uFF0C\u4E14<u>\u4E0D\u7528\u518D\u7531\u7A0B\u5E8F\u5458\u624B\u5DE5\u5C06\u7CFB\u7EDF\u7EA7\u670D\u52A1 \u201C \u6DF7\u6742 \u201D \u5230\u4E3B\u4E1A\u52A1\u903B\u8F91\u4E2D</u>\u4E86\uFF0C\u800C\u662F\u7531 Spring \u5BB9\u5668\u7EDF\u4E00\u5B8C\u6210 \u201C \u7EC7\u5165 \u201D\u3002</p></li></ul><blockquote><p>Spring \u662F\u4E8E 2003 \u5E74\u5174\u8D77\u7684\u4E00\u4E2A\u8F7B\u91CF\u7EA7\u7684 Java \u5F00\u53D1\u6846\u67B6\uFF0C\u5B83\u662F\u4E3A\u4E86\u89E3\u51B3\u4F01\u4E1A\u5E94\u7528\u5F00\u53D1\u7684\u590D\u6742\u6027\u800C\u521B\u5EFA\u7684\u3002Spring\u7684\u6838\u5FC3\u662F\u63A7\u5236\u53CD\u8F6C\uFF08IoC\uFF09\u548C\u9762\u5411\u5207\u9762\u7F16\u7A0B\uFF08AOP\uFF09\u3002\u7B80\u5355\u6765\u8BF4\uFF0CSpring \u662F\u4E00\u4E2A\u5206\u5C42\u7684 Java SE/EE full-stack\uFF08\u4E00\u7AD9\u5F0F\uFF09\u8F7B\u91CF\u7EA7\u5F00\u6E90\u6846\u67B6\u3002</p><p>\u2014\u2014\u767E\u5EA6\u767E\u79D1\u300ASpring\u300B</p></blockquote><h2 id="_1-1-spring\u4F53\u7CFB\u7ED3\u6784" tabindex="-1"><a class="header-anchor" href="#_1-1-spring\u4F53\u7CFB\u7ED3\u6784" aria-hidden="true">#</a> 1.1 Spring\u4F53\u7CFB\u7ED3\u6784</h2><img src="https://raw.githubusercontent.com/hahg2000/SSMPic/main/Spring\u4F53\u7CFB\u7ED3\u6784.png" style="zoom:70%;"><p>\u200B Spring \u7531 20 \u591A\u4E2A\u6A21\u5757\u7EC4\u6210\uFF0C\u5B83\u4EEC\u53EF\u4EE5\u5206\u4E3A</p><ul><li>\u6570\u636E\u8BBF\u95EE/\u96C6\u6210\uFF08Data Access/Integration\uFF09\uFF1B</li><li>Web\uFF1B</li><li>\u9762\u5411\u5207\u9762\u7F16\u7A0B\uFF08AOP, Aspects\uFF09\uFF1B</li><li>\u5E94\u7528\u670D\u52A1\u5668\u8BBE\u5907\u7BA1\u7406\uFF08Instrumentation\uFF09\uFF1B</li><li>\u6D88\u606F\u53D1\u9001\uFF08Messaging\uFF09\uFF1B</li><li>\u6838\u5FC3\u5BB9\u5668\uFF08Core Container\uFF09\uFF1B</li><li>\u6D4B\u8BD5\uFF08Test\uFF09\u3002</li></ul><h2 id="_1-2-spring-\u4E0B\u8F7D" tabindex="-1"><a class="header-anchor" href="#_1-2-spring-\u4E0B\u8F7D" aria-hidden="true">#</a> 1.2 Spring \u4E0B\u8F7D</h2><p>\u4E0B\u8F7D\u5730\u5740\uFF1A\u3010 https://repo.spring.io/release/org/springframework/spring/ \u3011</p><h2 id="_1-3-spring4\u7684jar\u5305\u8BF4\u660E" tabindex="-1"><a class="header-anchor" href="#_1-3-spring4\u7684jar\u5305\u8BF4\u660E" aria-hidden="true">#</a> 1.3 Spring4\u7684Jar\u5305\u8BF4\u660E</h2><p>\u200B \u89E3\u538B\u540E\u6587\u4EF6\u7684\u8BF4\u660E\u5982\u4E0B\u56FE\uFF1A</p><img src="https://raw.githubusercontent.com/hahg2000/SSMPic/main/Jar\u5305\u8BF4\u660E1.png" alt="s" style="zoom:80%;"><p>\u200B \u6253\u5F00 libs \u4F1A\u53D1\u73B0\u6709\u5F88\u591A\u6A21\u5757\u7684 Jar \u5305\uFF0C\u5176\u4E2D\u6BCF\u4E2A\u6A21\u5757\u90FD\u6709\u4E09\u7C7B\u538B\u7F29\u5305\uFF1A</p><img src="https://raw.githubusercontent.com/hahg2000/SSMPic/main/Jar\u5305\u8BF4\u660E2.png" style="zoom:80%;"><h2 id="_1-4-spring\u7684\u7279\u70B9" tabindex="-1"><a class="header-anchor" href="#_1-4-spring\u7684\u7279\u70B9" aria-hidden="true">#</a> 1.4 Spring\u7684\u7279\u70B9</h2><h3 id="_1-4-1-\u975E\u4FB5\u5165\u5F0F" tabindex="-1"><a class="header-anchor" href="#_1-4-1-\u975E\u4FB5\u5165\u5F0F" aria-hidden="true">#</a> 1.4.1 \u975E\u4FB5\u5165\u5F0F</h3><p>\u200B \u6240\u8C13\u975E\u4FB5\u5165\u5F0F\u662F\u6307\uFF0CSpring \u6846\u67B6\u7684 API \u4E0D\u4F1A\u5728\u4E1A\u52A1\u903B\u8F91\u4E0A\u51FA\u73B0\uFF0C\u5373\u4E1A\u52A1\u903B\u8F91\u662F POJO \uFF08Plain Old Java Object \uFF09\u3002\u7531\u4E8E\u4E1A\u52A1\u903B\u8F91\u4E2D\u6CA1\u6709 Spring \u7684 API\uFF0C\u6240\u4EE5\u4E1A\u52A1\u903B\u8F91\u53EF\u4EE5\u4ECE Spring \u6846\u67B6\u5FEB\u901F\u7684\u79FB\u690D\u5230\u5176\u4ED6\u6846\u67B6\uFF0C\u5373\u4E0E\u73AF\u5883\u65E0\u5173\u3002\u4F8B\u5982\uFF0C\u5728\u5B9E\u73B0\u63A5\u53E3\u6216\u7EE7\u627F\u7236\u7C7B\u65F6\uFF0C\u4E0D\u9700\u8981\u4F7F\u7528\u989D\u5916\u7684 Jar \u5305\uFF0C\u53EA\u9700\u8981\u4F7F\u7528 JDK \u5373\u53EF\uFF0C\u8FD9\u5C31\u662F\u4EE3\u7801\u672A\u88AB\u4FB5\u5165\u3002</p><h3 id="_1-4-2-\u5BB9\u5668" tabindex="-1"><a class="header-anchor" href="#_1-4-2-\u5BB9\u5668" aria-hidden="true">#</a> 1.4.2 \u5BB9\u5668</h3><p>\u200B Spring \u4F5C\u4E3A\u4E00\u4E2A\u5BB9\u5668\uFF0C\u53EF\u4EE5\u7BA1\u7406\u5BF9\u8C61\u7684\u751F\u547D\u5468\u671F\u3001\u5BF9\u8C61\u4E0E\u5BF9\u8C61\u4E4B\u95F4\u7684\u4F9D\u8D56\u5173\u7CFB\u3002\u53EF\u4EE5\u901A\u8FC7 <strong>\u914D\u7F6E\u6587\u4EF6</strong>\uFF0C\u6765\u5B9A\u4E49\u5BF9\u8C61\uFF0C\u4EE5\u53CA\u8BBE\u7F6E\u4E0E\u5176\u4ED6\u5BF9\u8C61\u7684\u4F9D\u8D56\u5173\u7CFB\u3002</p><h3 id="_1-4-3-ioc" tabindex="-1"><a class="header-anchor" href="#_1-4-3-ioc" aria-hidden="true">#</a> 1.4.3 IoC</h3><p>\u200B \u63A7\u5236\u53CD\u8F6C\uFF08Inversion of Control\uFF09\uFF0C\u5373\u521B\u5EFA\u88AB\u8C03\u7528\u8005\u7684\u5B9E\u4F8B\u4E0D\u662F\u7531\u8C03\u7528\u8005\u5B8C\u6210\uFF0C\u800C\u662F\u7531 Spring \u5BB9\u5668\u5B8C\u6210\uFF0C\u5E76\u6CE8\u5165\u8C03\u7528\u8005\u3002\u5F53\u5E94\u7528\u4E86 IoC\uFF0C\u4E00\u4E2A\u5BF9\u8C61\u4F9D\u8D56\u7684\u5176\u5B83\u5BF9\u8C61\u4F1A\u901A\u8FC7 <strong>\u88AB\u52A8</strong> \u7684\u65B9\u5F0F\u4F20\u9012\u8FDB\u6765\uFF0C\u800C\u4E0D\u662F\u8FD9\u4E2A\u5BF9\u8C61\u81EA\u5DF1\u521B\u5EFA\u6216\u8005\u67E5\u627E\u4F9D\u8D56\u5BF9\u8C61\u3002\u5373\u4E0D\u662F\u5BF9\u8C61\u4ECE\u5BB9\u5668\u4E2D\u67E5\u627E\u4F9D\u8D56\uFF0C\u800C\u662F<u>\u5BB9\u5668\u5728\u5BF9\u8C61\u521D\u59CB\u5316\u65F6\u4E0D\u7B49\u5BF9\u8C61\u8BF7\u6C42\u5C31\u4E3B\u52A8\u5C06\u4F9D\u8D56\u4F20\u9012\u7ED9\u5B83</u>\u3002</p><p>\u200B \u4F20\u7EDF\u7684\u5F00\u53D1\u65B9\u5F0F\u5C31\u5982\u540C\u6211\u4EEC\u559D\u725B\u5976\uFF0C\u5728\u4F60\u60F3\u559D\u7684\u65F6\u5019\u9700\u8981\u4E3B\u52A8\u5730\u5229\u7528\u5438\u7BA1\u5C06\u725B\u5976\u5438\u4E0A\u6765\u559D\uFF1B\u800C\u63A7\u5236\u53CD\u8F6C\u5C31\u5982\u4F55\u53BB\u533B\u9662\u6253\u9488\uFF0C\u53EA\u9700\u8981\u5F80\u6905\u5B50\u4E0A\u4E00\u5EA7\uFF0C\u533B\u751F\u5C31\u4F1A\u5E2E\u4F60\u6CE8\u5C04\u836F\u54C1\uFF0C\u4E0D\u9700\u8981\u81EA\u5DF1\u52A8\u624B\u3002</p><h3 id="_1-4-4-aop" tabindex="-1"><a class="header-anchor" href="#_1-4-4-aop" aria-hidden="true">#</a> 1.4.4 AOP</h3><p>\u200B \u9762\u5411\u5207\u9762\u7F16\u7A0B\uFF08AOP\uFF0CAspect Orient Programming\uFF09\uFF0C<span style="color:red;">\u662F\u4E00\u79CD\u7F16\u7A0B\u601D\u60F3\uFF0C\u662F\u9762\u5411\u5BF9\u8C61\u7F16\u7A0B OOP \u7684\u8865\u5145\u3002</span>\u5F88\u591A\u6846\u67B6\u90FD\u5B9E\u73B0\u4E86\u5BF9AOP\u7F16\u7A0B\u601D\u60F3\u7684\u5B9E\u73B0\u3002</p><p>\u200B Spring \u4E5F\u63D0\u4F9B\u4E86\u9762\u5411\u5207\u9762\u7F16\u7A0B\u7684\u4E30\u5BCC\u652F\u6301\uFF0C\u5141\u8BB8\u901A\u8FC7\u5206\u79BB\u5E94\u7528\u7684\u4E1A\u52A1\u903B\u8F91\u4E0E\u7CFB\u7EDF\u7EA7\u670D\u52A1\uFF08\u4F8B\u5982\u65E5\u5FD7\u548C\u4E8B\u52A1\u7BA1\u7406\uFF09\u8FDB\u884C\u5F00\u53D1\u3002\u5E94\u7528\u5BF9\u8C61\u53EA\u5B9E\u73B0\u5B83\u4EEC\u5E94\u8BE5\u505A\u7684\u2014\u2014\u5B8C\u6210\u4E1A\u52A1\u903B\u8F91\u2014\u2014\u4EC5\u6B64\u800C\u5DF2\uFF0C\u5B83\u4EEC\u5E76\u4E0D\u8D1F\u8D23\u5176\u5B83\u7684\u7CFB\u7EDF\u7EA7\u5173\u6CE8\u70B9\uFF0C\u4F8B\u5982\u65E5\u5FD7\u6216\u4E8B\u52A1\u652F\u6301\u3002</p><p>\u200B \u6211\u4EEC\u53EF\u4EE5\u628A\u65E5\u5FD7\u3001\u5B89\u5168\u3001\u4E8B\u52A1\u7BA1\u7406\u7B49\u670D\u52A1\u7406\u89E3\u6210\u4E00\u4E2A\u201C \u5207\u9762 \u201D\uFF0C\u90A3\u4E48\u4EE5\u524D\u8FD9\u4E9B\u670D\u52A1\u4E00\u76F4\u662F\u76F4\u63A5\u5199\u5728\u4E1A\u52A1\u903B\u8F91\u7684\u4EE3\u7801\u5F53\u4E2D\u7684\uFF0C\u8FD9\u6709\u4E24\u70B9\u4E0D\u597D\uFF1A</p><ol><li>\u9996\u5148\u4E1A\u52A1\u903B\u8F91\u4E0D\u7EAF\u51C0\uFF1B</li><li>\u5176\u6B21\u8FD9\u4E9B\u670D\u52A1\u88AB\u5F88\u591A\u4E1A\u52A1\u903B\u8F91\u53CD\u590D\u4F7F\u7528\uFF0C\u5B8C\u5168\u53EF\u4EE5\u5265\u79BB\u51FA\u6765\u505A\u5230\u590D\u7528\u3002</li></ol><p>\u200B \u90A3\u4E48 AOP \u5C31\u662F\u8FD9\u4E9B\u95EE\u9898\u7684\u89E3\u51B3\u65B9\u6848\uFF0C\u53EF\u4EE5\u628A\u8FD9\u4E9B\u670D\u52A1\u5265\u79BB\u51FA\u6765\u5F62\u6210\u4E00\u4E2A \u201C \u5207\u9762 \u201D\uFF0C\u4EE5\u671F\u590D\u7528\uFF0C\u7136\u540E\u5C06 \u201C \u5207\u9762 \u201D \u52A8\u6001\u7684 \u201C \u7EC7\u5165 \u201D \u5230\u4E1A\u52A1\u903B\u8F91\u4E2D\uFF0C<u>\u8BA9\u4E1A\u52A1\u903B\u8F91\u80FD\u591F\u4EAB\u53D7\u5230\u6B64 \u201C \u5207\u9762 \u201D \u7684\u670D\u52A1</u>\u3002\u5207\u9762\u5C31\u50CF\u4E66\u7B7E\uFF0C\u4E1A\u52A1\u903B\u8F91\u5C31\u50CF\u4E66\u672C\uFF0C\u5207\u9762\u7684\u63D2\u5165\u6216\u62FF\u8D70\u90FD\u4E0D\u4F1A\u5F71\u54CD\u4E1A\u52A1\u903B\u8F91\u7684\u8FD0\u884C\uFF0C\u800C\u4E14\u53EF\u4EE5\u53CD\u590D\u4F7F\u7528\uFF0C\u4F46\u6211\u80FD\u5229\u7528\u4E66\u7B7E\u6765\u8BB0\u5F55\u6211\u6BCF\u5929\u770B\u7684\u65F6\u95F4\u548C\u9875\u6570\u7B49\u6570\u636E\uFF0C\u4E1A\u52A1\u903B\u8F91\u4E5F\u53EF\u4EE5\u5229\u7528\u7CFB\u7EDF\u7EA7\u670D\u52A1\u6765\u8BA1\u5165\u4E00\u4E9B\u65E5\u5FD7\u7B49\u3002</p>',32),t=[p];function s(o,h){return i(),a("div",null,t)}var c=r(e,[["render",s],["__file","\u4E00\u3001Spring\u6982\u8FF0.html.vue"]]);export{c as default};
